package week3.day1;

public class GetCharacterAtIndex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String text = "Java Exercise";
		System.out.println("Given Text is" + " " + text);
		System.out.println("Index of Letter E is:" + " " + text.indexOf('E'));
		System.out.println("Index of Letter S is:" + " " + text.indexOf('s'));

	}

}
